CREATE TABLE lucky_draw(    id             bigint generated by default as identity,    name           varchar(25),    description    varchar(100),    max_entries    bigint NOT NULL DEFAULT '0',    entry_number   bigint NOT NULL DEFAULT '0',    mode           varchar(25) ,    categories     varchar(100),    tags           varchar(100),    status         varchar(25) NOT NULL DEFAULT 'ACTIVE',    deleted        smallint  NOT NULL DEFAULT '0',    start_time     timestamp,    end_time       timestamp,    CONSTRAINT id UNIQUE (id))